template.value:
declare
    stage_code number := 22;
    remind_days number := 0;
    condition_alias varchar(255) := 'DEFAULT';
    notification_name varchar(255) := 'CRIME_CLAIM_CASE_EXPIRES';
    allowed_days number := 0;
begin
    SETTINGS_UTIL.FETCH_SETTINGS(
            stage_code ,
            TO_DATE('2021-04-12', 'YYYY-MM-DD') ,
            condition_alias ,
            remind_days ,
            notification_name,
            allowed_days
        );
end;

SELECT *
FROM V$Locked_Object A, All_Objects B
WHERE A.Object_ID = B.Object_ID;
