template.value:
@Parameter(
            name = "Accept",
            in = ParameterIn.HEADER,
            description = "Если header не указан, то по умолчанию вернется json представление.",
            examples = {
                    @ExampleObject(
                            name = "Accept json",
                            value = "application/json"
                    ),
                    @ExampleObject(
                            name = "Accept xml",
                            value = "application/xml"
                    )
            }
    )
